class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        int ans=-1;
        auto check=[&](int k)->bool{
            for(int i=0;i<n;i++){
                int t=nums[i];
                bitset<1001>dp;
                dp.reset();
                dp[0]=1;
                for(int j=0;j<k;j++){
                    int L=queries[j][0],R=queries[j][1],val=queries[j][2];
                    if(i>=L && i<=R){
                        dp|=(dp<<val);
                    }
                }
                if(!dp[t]) return false;
            }
            return true;;
        };
        int low=0,high=queries.size();
        while(low<=high){
            int mid=(low+high)>>1;
            if(check(mid)){
                high=mid-1;
                ans=mid;
            }
            else{
                low=mid+1;
            }
        }
        return ans;
    }
};

