class Solution {
    private boolean[][] dp;
    private int[] nums;
    private int[][] queries;
    private int n, Q;
    public int minZeroArray(int[] nums, int[][] queries) {
        this.nums = nums;
        this.queries = queries;
        this.n = nums.length;
        this.Q = queries.length;
        this.dp = new boolean[n][1001];
        for (int j = 0; j < n; j++) {
            Arrays.fill(dp[j], false);
            dp[j][0] = true;
        }
        if (allZero(nums)) return 0;
        return f(0);
    }

    private int f(int k) {
        if (k >= Q) return -1;
        int l = queries[k][0];
        int r = queries[k][1];
        int v = queries[k][2];
        for (int j = l; j <= r; j++) {
            boolean[] temp = Arrays.copyOf(dp[j], 1001);
            for (int pos = 0; pos + v < 1001; pos++) {
                if (dp[j][pos]) {
                    temp[pos + v] = true;
                }
            }
            for (int pos = nums[j] + 1; pos < 1001; pos++) {
                temp[pos] = false;
            }
            dp[j] = temp;
        }
        if (allAchieved()) return k + 1;
        return f(k + 1);
    }
    private boolean allZero(int[] nums) {
        for (int num : nums) {
            if (num != 0) return false;
        }
        return true;
    }
    private boolean allAchieved() {
        for (int j = 0; j < n; j++) {
            if (!dp[j][nums[j]]) return false;
        }
        return true;
    }
}

