class Solution {
public:
 bool chk(int ind,vector<int>&a,int k,vector<vector<int>> &dp){
      if(k==0) return 1;
      if(ind<0||k<0) return 0;
      if(dp[ind][k]!=-1) return dp[ind][k];
      int x = chk(ind-1,a,k-a[ind],dp);
      int y = chk(ind-1,a,k,dp);
      if(k<0) return (x|y);
      return dp[ind][k]=(x||y);
 }
 bool fun(vector<int>& nums, vector<vector<int>>& q,int k){
        int n = nums.size();
        for(int i=0;i<n;i++){
            int tar=nums[i];
            if(tar==0) continue;
            vector<int> v;
            for(int j=0;j<k;j++){
                if(q[j][0]<=i&&i<=q[j][1]){
                    v.push_back(q[j][2]);
                }
            }
            vector<vector<int>> dp(v.size(),vector<int>(tar+1,-1)); 
            if(!chk(v.size()-1,v,tar,dp)) return 0;
        }
      return 1;
    }
    int minZeroArray(vector<int>& nums,vector<vector<int>>& q) {
        int l=0,r=q.size(),res=-1;
        while(l<=r){
             int mid=(l+r)/2;
             bool f=fun(nums,q,mid);
             if(f){
                  res=mid;
                  r=mid-1;
             }
            else l=mid+1;
        }
        return res;
    }
};

