class Solution {
public:
    bool build(vector<int>& arr, int x) {
        int n = arr.size();
        vector<vector<bool>>dp(x+1,vector<bool>(n+1,false));
        dp[0] = vector<bool>(n+1,true);
        // for(int i =0; i <= x; i++){
        //     dp[i][0] = true;
        // }

        for(int i = 1; i <= x; i++) {
            for(int j = 1; j <= n; j++) {
                dp[i][j] = dp[i][j-1];
                if(arr[j-1] <= i) {
                    dp[i][j] = (dp[i][j] || dp[i-arr[j-1]][j-1]);
                }
            }
        }
        return dp[x][n];
    }

    bool pos(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<vector<int>>comb(n);

        for(int i = 0; i < k; i++) {
            for(int j = queries[i][0]; j <= queries[i][1]; j++){
                comb[j].push_back(queries[i][2]);
            }
        }
        for(int i = 0; i < n; i++) {
            if(!build(comb[i],nums[i]))return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int k = 0;

        int low = 0;
        int high = queries.size();
        int mid;
        int res = high;
        while(low <= high) {
            mid = (low+high)/2;
            if(pos(nums,queries,mid)){
                high = mid -1;
                res = min(res,mid);
            }else{
                low = mid+1;
            }
        }
        if(low > queries.size())return -1;
        // cout<<low<<' '<<high<<endl;
        return res;
    }
};

