class Solution:
    def find(self, i, dp, dec, kv, curr):
        if curr < 0: return float('inf')
        if curr == 0: return -1
        if i == len(dec): return float('inf')
        if dp[curr][i] != -1: return dp[curr][i]
        c1 = self.find(i+1, dp, dec, kv, curr)
        c2 = self.find(i+1, dp, dec, kv, curr - dec[i])
        if c2 != float('inf'):
            c2 = max(kv[i], c2)
        dp[curr][i] = min(c1, c2)
        return dp[curr][i]

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        k_vals = [[] for _ in range(n)]
        dec = [[] for _ in range(n)]
        for i, (l, r, val) in enumerate(queries):
            for j in range(l, r+1):
                dec[j].append(val)
                k_vals[j].append(i)
        k = 0
        for i in range(n):
            if nums[i] == 0: continue
            dp = [[-1] * (len(dec[i])) for _ in range(nums[i] + 1)]
            res = self.find(0, dp, dec[i], k_vals[i], nums[i])
            if res == float('inf'): return -1
            k = max(k, res + 1)
        return k

