        int n = nums.size();
        int q = queries.size();

        int sum = 0;
        for(int i=0; i<n; i++){
            sum += nums[i];
        }
        if(sum == 0){
            return 0;
        }

        vector<vector<pair<int, int>>> arr(n);
        for(int i=0; i<q; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];

            for(int j=l; j<=r; j++){
                arr[j].push_back({v, i});
            }
        }

        for(int k=1; k<=q; k++){
            bool flag = true;
            for(int i=0; i<n&&flag; i++){
                if(nums[i]==0) continue;
                vector<int> v;
                int sum = 0;
                for(auto it : arr[i]){
                    int val = it.first;
                    int ind = it.second;

                    if(ind < k){
                        v.push_back(val);
                        sum += val;
                    }
                }

                if(sum < nums[i]){
                    flag = false;
                    break;
                }

                if(!solve(v, nums[i])){
                    flag = false;
                    break;
                }
            }

            if(flag){
                return k;
            }
        }

        return -1;
    }
};

