class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        int res = -1;
        for (int i = 0; i < n; i++) {
            int ans = -1;
            vector<vector<bool>> dp(q + 1, vector<bool>(nums[i] + 1, false));
            for (int r = 0; r <= q; r++) {
                dp[r][0] = true;
            }
            for (int c = 1; c <= nums[i]; c++) {
                for (int r = 1; r <= q; r++) {
                    if (i >= queries[r - 1][0] && i <= queries[r - 1][1] &&
                        c >= queries[r - 1][2]) {
                        dp[r][c] =
                            dp[r - 1][c] || dp[r - 1][c - queries[r - 1][2]];
                    } else {
                        dp[r][c] = dp[r - 1][c];
                    }
                }
            }
            for (int r = 0; r <= q; r++) {
                if (dp[r][nums[i]]) {
                    ans = r;
                    break;
                }
            }
            if (ans == -1) {
                return -1;
            }
            res = max(res, ans);
        }
        return res;
    }
};

