class Solution {
    public int minZeroArray(int[] nums, int[][] qs) {

        int ln = qs.length;

        int l = 0;
        int r = ln;

        int res = -1;
        while(l <= r){

            int mid = ((r - l) / 2) + l;
            if(poss(mid,nums,qs)){
                res = mid;
                r = mid - 1;
            }else{
                l = mid + 1;   
            }

        }
        // if(res  != -1) res++;/
        return res;
    }


    private boolean poss(int md,int[] nums,int[][] qs){


        int m = qs.length;

        List<Integer>[][] diff = new ArrayList[12][2];


        for(List<Integer>[] aa : diff){
            for(int i =0;i<2;i++){
                aa[i] = new ArrayList<>();
            }
        }
        for(int j = 0;j<md;j++){
            int[] q = qs[j];
            diff[q[0]][0].add(q[2]);
            diff[q[1]+1][1].add(q[2]);

        }

        List<Integer> arr = new ArrayList<>();
        for(int i =0;i<nums.length;i++){
            for(Integer e : diff[i][0]){
                arr.add(e);
            }
            for(Integer e : diff[i][1]){
                arr.remove(e);
            }
            // System.out.println(arr);
            int[][] dp = new int[arr.size() + 1][nums[i] + 1];


            if(!rec(0,nums[i],dp,arr)){

                return false;
            }


        }

        return true;


    }

    private boolean rec(int i,int sum,int[][] dp,List<Integer> arr){

        if(sum == 0) return true;
        if(i >= arr.size()) return false;

        if(dp[i][sum] != 0) return dp[i][sum] == 1;
        boolean res = rec(i+1,sum,dp,arr);
        if(sum >= arr.get(i)){

            res = res || rec(i+1,sum - arr.get(i),dp,arr);
        }
        dp[i][sum] = res? 1: 2;
        return res;
    }





}

