class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size(), target, lo = 0, hi = m + 1;
        auto check = [&](int k) -> bool {
            for (int i = 0; i < n; ++i) {
                target = nums[i];
                if (target == 0) continue;
                vector<int> available;
                for (int j = 0; j < k; j++) {
                    int l = queries[j][0], r = queries[j][1], v = queries[j][2];
                    if (l <= i && i <= r) available.push_back(v);
                }
                if (!subsetSumExact(available, target)) return false;
            }
            return true;
        };
        while (lo < hi) {
            int mid = lo + (hi - lo) / 2;
            if (check(mid)) hi = mid;
            else lo = mid + 1;
        }
        return lo > m ? -1 : lo;
    }
    bool subsetSumExact(const vector<int>& available, int target) {
        vector<bool> dp(target + 1, false);
        dp[0] = true;
        for (int v : available) {
            for (int s = target; s >= v; s--) {
                if (dp[s - v]) dp[s] = true;
            }
        }
        return dp[target];
    }
};

