class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)

        dp = [set([0]) for _ in range(n)]

        if all(num == 0 for num in nums):
            return 0

        for k, (l, r, v) in enumerate(queries):
            for j in range(l, r+1):
                n_dp = {x+v for x in dp[j] if x + v <= 1000}
                dp[j] |= n_dp

                dp[j] = {x for x in dp[j] if x <= nums[j]}

            if all(nums[j] in dp[j] for j in range(n)):
                return k + 1

        return -1

