public:
    int dp[1010][1010];

    int rec(int i,int val,vector<pair<int,int>>&arr){
        // base conditon
        // if(val == 0){
        //     return i;
        // }

        if(val < 0){
            return 1e6;
        }

        if(i == arr.size()){
            if(val != 0){
                return 1e6;
            }
            // return i;
        }

        // prunnig
        if(val - arr[i].first == 0){
            return arr[i].second;
        }

        // cache check
        if(dp[i][val] != -1){
