import java.util.Arrays;
import java.util.List;
import java.util.ArrayList;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, m = queries.length;
        List<List<Pair<Integer,Integer>>> xx = new ArrayList<>();

        for (int i = 0; i < n; i++) xx.add(new ArrayList<>());

        for (int j = 0; j < m; j++) {
            int li = queries[j][0];
            int ri = queries[j][1];
            int vali = queries[j][2];
            for (int i = li; i <= ri; i++) {
                xx.get(i).add(new Pair<Integer, Integer>(vali,j));
            }
        }

        // Step 2: DP to determine the min queries required for each nums[i]
        int maxQueries = 0;

        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) continue;

            List<Pair<Integer,Integer>> values = xx.get(i);
            int[] dp = new int[target + 1];
            Arrays.fill(dp, Integer.MAX_VALUE / 2);
            dp[0] = 0;
            //System.out.println(values);
            // Compute min queries needed to make nums[i] zero
            int pp=-1;
            for (int ii=0;ii<values.size();ii++) {
                Pair<Integer,Integer> vv=values.get(ii);
                int v=vv.getKey();
                for (int j = target; j >= v; j--) {
                    dp[j] = Math.min(dp[j], dp[j - v] + 1);
                }
                if(dp[target]!=Integer.MAX_VALUE / 2){
                    pp=vv.getValue();
                    break;
                }
            }
            if(pp!=-1)pp++;

            if (pp == -1) return -1;
            maxQueries = Math.max(maxQueries, pp);
        }

        return maxQueries;
    }
}

