#include <vector>
using namespace std;
#define rep(i,a,b) for (int i = (a); i < (b); i++)

#define ll long long
#define ar array
#define pb push_back

class Solution {
public:
    bool canZero(int k, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> coins(n);
        rep(j, 0, k) {
            int l = queries[j][0], r = queries[j][1], v = queries[j][2];
            rep(i, l, r + 1) {
                coins[i].pb(v);
            }
        }
        rep(i, 0, n) {
            if(nums[i] == 0)
                continue;
            int target = nums[i];
            int sumCoins = 0;
            for(auto coin : coins[i]) 
                sumCoins += coin;
            if(sumCoins < target)
                return false;
            vector<bool> dp(target+1, false);
            dp[0] = true;
            for(auto coin : coins[i]) {
                for (int s = target; s >= coin; s--) {
                    if(dp[s-coin])
                        dp[s] = true;
                }
            }
            if(!dp[target])
                return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();
        bool alreadyZero = true;
        for (int x : nums) {
            if (x != 0) { alreadyZero = false; break; }
        }
        if (alreadyZero)
            return 0;

        int lo = 0, hi = m+1, ans = -1;
        while (lo < hi) {
            int mid = lo + (hi - lo) / 2;
            if (canZero(mid, nums, queries)) {
                ans = mid;
                hi = mid;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }
};

