class Solution {
public:

    bool helper(vector<int> &nums, vector<vector<int>> &queries, int k) {
        int n = nums.size();
        vector<vector<bool>> dp(nums.size(), vector<bool>(1001, false));
        for(int i=0;i<n;i++) {
            dp[i][0] = true;
        }

        for(int i=0;i<k;i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for(int j=l;j<=r;j++) {
                for(int s = 1000-val; s >= 0; s--) {
                    if(dp[j][s]) {
                        dp[j][s+val] = true;
                    }
                }
            }
        }

        for(int j=0;j<n;j++) {
            if(dp[j][nums[j]] == false) {
                return false;
            }
        }

        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool allZeros = true;
        for(int i=0;i<nums.size();i++) {
            if(nums[i] != 0) {
                allZeros = false;
                break;
            }
        }
        if(allZeros) {
            return 0;
        }
        int n = queries.size();
        int low = 1, high = n;
        int ans = -1;
        while(low <= high) {
            int mid = low + (high - low)/2;
            if(helper(nums, queries, mid)) {
                ans = mid;
                high = mid-1;
            }
            else {
                low = mid + 1;
            }
        }
        return ans;
    }
};

