class Solution {
    private boolean canMakeZero(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) continue;
            boolean[] dp = new boolean[target + 1];
            dp[0] = true;
            for (int j = 0; j < k; j++) {
                int l = queries[j][0], r = queries[j][1], coin = queries[j][2];
                if (i >= l && i <= r) {
                    for (int x = target; x >= coin; x--) {
                        if (!dp[x] && dp[x - coin]) {
                            dp[x] = true;
                        }
                    }
                }
            }
            if (!dp[target]) return false;
        }
        return true;
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int m = queries.length;
        int n = nums.length;
        boolean alreadyZero = true;
        for (int num : nums) {
            if (num != 0) {
                alreadyZero = false;
                break;
            }
        }
        if (alreadyZero) return 0;
        int low = 0, high = m + 1; 
        while (low < high) {
            int mid = low + (high - low) / 2;
            if (canMakeZero(nums, queries, mid)) {
                high = mid; 
            } else {
                low = mid + 1;
            }
        }

        return (low <= m) ? low : -1;
    }
}

