class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        vector<vector<int>> cover(n);

        for (int q = 0; q < m; q++) {
            for (int i = queries[q][0]; i <= queries[q][1]; i++) {
                cover[i].push_back(q);
            }
        }

        int maxVal = *max_element(nums.begin(), nums.end());
        const int T = maxVal;

        auto check = [&](int k) -> bool {
            for (int i = 0; i < n; i++) {
                bitset<10001> dp;
                dp[0] = 1;
                for (int q : cover[i]) {
                    if (q >= k) continue;
                    dp |= (dp << queries[q][2]);
                }
                if (!dp[nums[i]]) return false;
            }
            return true;
        };

        int l = 0, r = m, ans = -1;
        while (l <= r) {
            int mid = l + (r - l) / 2;
            if (check(mid)) {
                ans = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

