class Solution {
public:
    bool isSubsetSum(vector<int> arr, int sum) {
        int n = arr.size();
        vector<bool> prev(sum + 1, false), curr(sum + 1);

        // Mark prev[0] = true as it is true
          // to make sum = 0 using 0 elements
        prev[0] = true;

        // Fill the subset table in
          // bottom up manner
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= sum; j++) {
                if (j < arr[i - 1])
                    curr[j] = prev[j];
                else
                    curr[j] = (prev[j] || prev[j - arr[i - 1]]);
            }
            prev = curr;
        }
        return prev[sum];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int s=0,e=queries.size()-1, res=-2,isZero=1;

        for(int i=0;i<nums.size();i++){
            if(nums[i]!=0){
                isZero=0;
                break;
            }
        }

        if(isZero) return 0;

        while(s<=e){
            int mid=s+(e-s)/2;

            vector<vector<int> > v(nums.size());

            for(int i=0;i<nums.size();i++){
                for(int j=0;j<=mid;j++){
                    if(queries[j][0]<=i && queries[j][1]>=i){
                        v[i].push_back(queries[j][2]);
                    }
                }
            }

            bool f=true;
            // cout<<mid<<endl;
            //  for(int i=0;i<nums.size();i++){
            //     for(int j=0;j<v[i].size();j++){
            //         cout<<v[i][j]<<" ";
            //     }
            //     cout<<endl;
            //  }
            // cout<<endl;

            for(int i=0;i<nums.size();i++){
                if(!isSubsetSum(v[i],nums[i])){
                    f=false;
                    break;
                }
            }
            if(f){
                res=mid;
                e=mid-1;
            }
            else{
                s=mid+1;
            }
        }
        return res+1;
    }
};

