class Solution {
public:
    bool sum(const vector<int>& a, int m, int x) {
        vector<bool> dp(x + 1, false);
        dp[0] = true;

        for (int i = 0; i < m; ++i) {
            for (int j = x; j >= a[i]; --j) {
                dp[j] = dp[j] || dp[j - a[i]];
            }
        }

        return dp[x];
    }

    int minZeroArray(vector<int>& a, vector<vector<int>>& qu) {
        int n = a.size();

        int check = count(a.begin(), a.end(), 0);
        if(check == n) return 0;

        vector<vector<int>> pos(n);
        for(auto v: qu) {
            for(int i=0; i<n; i++) {
                if(i >= v[0] && i<= v[1]) {
                    pos[i].push_back(v[2]);
                }
                else {
                    pos[i].push_back(0);
                }
            }
        }

        int final = -1;
        int s = 0, e = qu.size() - 1;
        while(s <= e) {
            int ans = (s + e) / 2;

            int cnt = 0;
            for(auto x : a) {
                if(x == 0) {
                    cnt++;
                    continue;
                }
                if(sum(pos[cnt], min(ans + 1, int(pos[cnt].size())), x)) {
                    cnt++;
                } else {
                    break;
                }
            }

            if(cnt == n) {
                final = ans+1;
                e = ans - 1;
            } else {
                s = ans + 1;
            }
        }

        return final;
    }
};

