class Solution {
public:
    bool fun(int m,vector<int>& nums, vector<vector<int>>& q){
        int n=nums.size();
        for(int i=0;i<n;i++){
            int t=nums[i];
            if(t==0)continue;
            vector<bool>dp(t+1,false);
            dp[0]=true;
            for(int j=0;j<m;j++){
                int l=q[j][0],r=q[j][1],v=q[j][2];
                if(l<=i && i<=r){
                    for(int s=t;s>=v;s--){
                        if(dp[s-v])dp[s]=true;
                    }
                }
            }
            if(!dp[t])return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int m=q.size();
        int l=0,r=m+1;
        while(l<r){
            int mid=(l+r)/2;
            if(fun(mid,nums,q)){
                r=mid;
            }else l=mid+1;
        }
        if(l>m)return -1;
        return l;
    }
};

