class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        next_query_index = 0
        n = len(queries)

        for i, num in enumerate(nums):
            if num == 0:
                continue
            sums = 1  # for 0
            look_for = 1 << num
            for j in range(n):
                if j == next_query_index:
                    next_query_index += 1
                l, r, val = queries[j]
                if not (l <= i <= r):
                    continue
                sums |= sums << val
                if sums & look_for:  # num can be made as a sum of vals
                    break
            else:
                return -1
        return next_query_index

