class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all(x == 0 for x in nums):
            return 0

        result = -1
        for idx in range(len(nums)):
            if nums[idx] == 0:
                continue
            target_val = nums[idx]
            relevant_queries = []
            for q_idx, (left, right, val) in enumerate(queries):
                if left <= idx <= right:
                    relevant_queries.append((q_idx, val))
            if not relevant_queries:
                return -1

            dp_state = {0: -1}
            for q_idx, val in relevant_queries:
                new_dp = {}
                for sum_val, max_q in dp_state.items():
                    if sum_val in new_dp:
                        if max_q < new_dp[sum_val]:
                            new_dp[sum_val] = max_q
                    else:
                        new_dp[sum_val] = max_q
                    new_sum = sum_val + val
                    if new_sum > target_val:
                        continue
                    new_max_q = max(max_q, q_idx)
                    if new_sum in new_dp:
                        if new_max_q < new_dp[new_sum]:
                            new_dp[new_sum] = new_max_q
                    else:
                        new_dp[new_sum] = new_max_q
                dp_state = new_dp

            if target_val not in dp_state:
                return -1
            current_max_q = dp_state[target_val]
            if current_max_q > result:
                result = current_max_q

        return result + 1 if result != -1 else 0

