class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();

        //  0 0 ()
        bool alreadyZero = true;
        for(int x : nums){
            if(x != 0) { alreadyZero = false; break; }
        }
        if(alreadyZero) return 0;

        // coverage[i] = {(qIndex, val)}  qIndex  (0-based)  i val
        vector<vector<pair<int,int>>> coverage(n);

        // 1.  coverage ( l,r  1-based l-1, r-1)
        for(int j = 0; j < q; j++){
            int l = queries[j][0];
            int r = queries[j][1];
            int v = queries[j][2];
            //  1-based:
            // l--; r--;
            for(int i = l; i <= r; i++){
                coverage[i].push_back({j, v});
            }
        }

        // 2.  k=1..q
        for(int k = 1; k <= q; k++){
            bool allOk = true; 
            for(int i = 0; i < n; i++){
                int target = nums[i];
                if(target == 0) continue; // 

                //  val ( < k)
                vector<int> vals;
                for(auto &p : coverage[i]){
                    if(p.first < k){
                        vals.push_back(p.second);
                    }
                }
                //  target
                if(!canMakeSum(vals, target)) {
                    allOk = false;
                    break;
                }
            }
            if(allOk) {
                return k; //  k
            }
        }

        // 
        return -1;
    }

private:
    //  bitset DP  vals  = target
    bool canMakeSum(const vector<int>& vals, int target) {
        const int MAX_SUM = 1000; //  constraints
        static bitset<MAX_SUM+1> dp;
        dp.reset();
        dp[0] = 1; 
        for(int v : vals){
            dp |= (dp << v);
            if(dp[target]) return true; // 
        }
        return dp[target];
    }
};

