class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        if not any(nums):
            return 0

        def subset_sum(A, target):
            @cache
            def recurse(idx,curr=0):
                if curr==target:
                    return True
                elif idx==len(A):
                    return False
                elif curr > target:
                    return False
                return recurse(idx+1, curr) or recurse(idx+1, curr+A[idx])

            res = recurse(0,0)
            recurse.cache_clear()
            return res

        def can_form(k):
            have = [[] for _ in range(len(nums))]
            for l,r,val in itertools.islice(queries,0,k+1,None):
                for idx in range(l,r+1):
                    have[idx].append(val)

            for idx,A in enumerate(have):
                have[idx] = sorted(A)
            # Find if you can make subset sums
            for target,A in zip(nums, have):
                if not target:
                    continue
                if target > sum(A):
                    return False
                if target == sum(A):
                    continue
                if not subset_sum(A,target):
                    return False
            return True

        res = bisect.bisect_left(range(len(queries)), True, key=can_form)+1
        if res > len(queries):
            return -1
        return res

