#include <vector>
#include <unordered_map>
#include <algorithm>

using namespace std;

class Solution {
public:
    bool solve(int i, vector<int>& p, int sum, vector<vector<int>>& dp) {
        if (sum == 0) return true;
        if (i >= p.size() || sum < 0) return false;
        if (dp[i][sum] != -1) return dp[i][sum];

        bool include = solve(i + 1, p, sum - p[i], dp);
        bool exclude = solve(i + 1, p, sum, dp);

        return dp[i][sum] = (include || exclude);
    }

    bool check(int j, vector<int>& nums, vector<vector<int>>& s) {
        unordered_map<int, vector<int>> mp;
        for (int k = 0; k <= j; k++) {
            int l = s[k][0], r = s[k][1];
            for (int i = l; i <= r; i++) {
                  mp[i].push_back(s[k][2]);
            }
        }
        for (int i = 0; i < nums.size(); i++) {
            vector<int>& p = mp[i];
            int sum = nums[i];
            if (sum != 0 && p.empty()) return false;

            vector<vector<int>> dp(p.size()+1, vector<int>(sum + 1, -1));
            if (!solve(0, p, sum, dp)) {
                return false;
            }
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& s) {
        int n = nums.size(), m = s.size();
        bool b=0;
        for(int i=0;i<nums.size();i++){
            if(nums[i]!=0) b=1;
        }
        if(b==0) return 0;
        int lo = -1, hi = m;

        while (hi - lo > 1) {
            int mid = (hi + lo) / 2;
            if (check(mid, nums, s)) {
                hi = mid;
            } else {
                lo = mid;
            }
        }

        return hi == m ? -1 : hi + 1;
    }
};

