class Solution {

    bool isValid(vector<int>& nums, vector<vector<int>>& queries, int k){
        int n = nums.size();
        vector<set<int>> vals(n);
        vector<bool> found(n);
        for(int i = 0; i < n; i++){
            vals[i].insert(0);
            if(nums[i] == 0){
                found[i] = true;
            }
        }

        for(int i = 0; i < k; i++){
            int left = queries[i][0], right = queries[i][1], val = queries[i][2];
            for(int idx = left; idx <= right; idx++){
                if(found[idx]){
                    continue;
                }
                set<int> newVals;
                newVals.insert(val);
                for(auto& it : vals[idx]){
                    newVals.insert(it + val);
                }

                vals[idx].insert(newVals.begin(), newVals.end());
                if(vals[idx].find(nums[idx]) != vals[idx].end()){
                    found[idx] = true;
                }
            }
        }

        for(int i = 0; i < n; i++){
            if(!found[i]){
                return false;
            }
        }

        return true;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();
        int low = 0, high = m;

        if(!isValid(nums, queries, m)) return -1;

        while(low <= high){
            int mid = (low + high) / 2;

            if(isValid(nums, queries, mid)){
                high = mid - 1;
            }
            else{
                low = mid + 1;
            }
        }

        return low;
    }
};

