class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), k = queries.size();
        bool zero = true;
        for(int i : nums) if(i != 0) zero = false;
        if(zero) return 0;
        vector<unordered_set<int>> opt(n);
        vector<bool> good(n, false);
        for(int i = 0; i < n; ++i) {
            opt[i].insert(0);
            if(nums[i] == 0) good[i] = true;
        }
        for(int i = 0; i < k; ++i) {
            for(int j = queries[i][0]; j <= queries[i][1]; ++j) {
                int v = queries[i][2];
                if(!good[j]) {
                    vector<int> tmp;
                    for(int k : opt[j]) tmp.push_back(k + v);
                    for(int k : tmp) {
                        if(k == nums[j]) {
                            good[j] = true;
                            break;
                        }else opt[j].insert(k);
                    }
                }
            }
            bool flag = true;
            for(bool b : good) if(!b) flag = false;
            if(flag) return ++i;
        }
        return -1;
    }
};

