class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        all_zero = True
        for num in nums:
            if num != 0:
                all_zero = False
                break

        if all_zero:
            return 0

        high = max(nums)
        dp = [[False] * (high + 1) for _ in range(n)]

        for i in range(n):
            dp[i][0] = True

        def can_transform():
            for i in range(n):
                if not dp[i][nums[i]]:
                    return False
            return True

        for i in range(len(queries)):
            l, r, num = queries[i]

            for j in range(l, r + 1):
                for k in range(high, num - 1, -1):
                    if dp[j][k - num]:
                        dp[j][k] = True

            if can_transform():
                return i + 1

        return -1

