class Solution {
public:
    bool f(vector<int>& nums, int k) {
        vector<bool> dp(k+1,0);
        dp[0]=1;
        for(auto it:nums){
            for(int j=k;j>=it;j--){
                dp[j]=(dp[j]||dp[j-it]);
            }
        }
        return dp[k];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),q=queries.size(),low=0,high=queries.size(),ans=1e9;
        while(low<=high){
            int mid=low+(high-low)/2;
            vector<vector<int>> v(n);
            for(int i=0;i<mid;i++){
                int start=queries[i][0],end=queries[i][1],val=queries[i][2];
                for(int j=start;j<=end;j++) v[j].push_back(val);
            }
            bool flag=1;
            for(int i=0;i<n && flag;i++){
                int ele=nums[i];
                if(!f(v[i],ele)) flag=0;
            }
            if(flag) {ans=min(ans,mid);high=mid-1;}
            else low=mid+1;
        }
        return ans==1e9?-1:ans;
    }
};

