class Solution {
public:
    bool funn1(vector<int>& nums, int n, vector<vector<int>>& q, int k) {
        int sum = 0;
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) {
                continue;
            }
            vector<int> diff;
            for (int j = 0; j < k; j++) {
                int s = q[j][0];
                int e = q[j][1];
                int val = q[j][2];
                if(i >= s && i<= e){
                    diff.push_back(val);
                }

            }
            vector<bool>dp(target+1,false);
            dp[0]=true;
            for(int v: diff){
                for(int s=target ;s>=v;s--){
                    if(dp[s-v]){
                        dp[s]=true;
                    }
                }
            }
            if(!dp[target]){
                return false;
            }
        }

        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), left = 0, right = queries.size();
        int ans = -1;
        if (funn1(nums, n, queries, right)) {
            while (left <= right) {
                int mid = left + (right - left) / 2;
                if (funn1(nums, n, queries, mid)) {
                    ans = mid;
                    right = mid - 1;
                } else {
                    left = mid + 1;
                }
            }
            return ans;
        } 
        else {
            return -1;
        }
    }
};

