class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        bool allZero = true;
        for(int i=0;i<n;i++)
            {
                if(nums[i]!=0)
                {
                    allZero = false;
                    break;
                }
            }
        if(allZero) return 0;
        vector<vector<pair<int,int>>> temp(n);
        for(int i=0;i<q;i++)
            {
                int l = queries[i][0], r = queries[i][1] , v = queries[i][2];
                for(int j=l;j<=r;j++) temp[j].push_back({i,v});
            }
        int ans = 0;
        for(int i=0;i<n;i++)
            {
                int target = nums[i];
                vector<int> dp(target+1,INT_MAX);
                dp[0] = -1;
                for(auto it : temp[i])
                    {
                        int idx = it.first;
                        int value = it.second;
                        for(int j=target-value;j>=0;j--)
                            {
                                if(dp[j]!=INT_MAX)
                                {
                                    int maxi = max(dp[j],idx);
                                    if(j+value<=target) dp[j+value] = min(dp[j+value],maxi);
                                }
                            }
                    }
                if(dp[target]==INT_MAX) return -1;
                ans = max(ans,dp[target]);
            }
