            int mid=(lo+hi)/2;
            vector<vector<int>>b(n);
        for(int i=0;i<mid;i++){
            int l=queries[i][0],r=queries[i][1],val=queries[i][2];
            for(int j=l;j<=r;j++){
                b[j].push_back(val);
            }
        }
            bool flag=0;
            for(int i=0;i<n;i++){
                vector<vector<int>>dp(b[i].size(),vector<int>(a[i]+1,-1));
                if(!f(0,a[i],b[i],dp)){
                    flag=1;
                    break;
                }
            }
            if(!flag){
                ans=mid;
                hi=mid-1;
            }
            else lo=mid+1;
        }
        return ans;
    }
