class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)


        dp = []
        for i in range(n):
            target = nums[i]

            cur = [inf] * (target + 1)
            cur[0] = 0
            dp.append(cur)


        for j, (l, r, val) in enumerate(queries):
            query_count = j + 1

            for i in range(l, r + 1):
                target = nums[i]

                if dp[i][target] != inf:
                    continue

                for s in range(target - val, -1, -1):
                    if dp[i][s] != inf:
                        dp[i][s + val] = min(dp[i][s + val], query_count)


        ans = 0
        for i in range(n):
            if dp[i][nums[i]] == inf:
                return -1
            ans = max(ans, dp[i][nums[i]])
        return ans

