class Solution {
public:
    bool check(vector<vector<int>>& diff, int& i, int now, int j) {
        if (now == 0) {
            return true;
        }
        if (now < 0 || j >= diff[i].size()) {
            return false;
        }
        return check(diff, i, now - diff[i][j], j + 1) || check(diff, i, now, j + 1);
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> diff(n);
        int k = 0;
        for (int i = 0; i < n; i++) {
            int sum = 0;
            for (int j = 0; j < diff[i].size(); j++) {
                sum += diff[i][j];
            }
            while (sum < nums[i] || !check(diff, i, nums[i], 0)) {
                if (k >= queries.size()) {
                    return -1;
                }
                for (int j = queries[k][0]; j <= queries[k][1]; j++) {
                    diff[j].push_back(queries[k][2]);
                }
                if (queries[k][0] <= i && queries[k][1] >= i) {
                    sum += queries[k][2];
                }
                k++;
            }
        }
        return k;
    }
};

