import java.util.ArrayList;
import java.util.BitSet;
import java.util.List;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;

        BitSet[] dp = new BitSet[n];
        for (int j = 0; j < n; j++) {
            dp[j] = new BitSet(1001);
            dp[j].set(0, true);
        }

        boolean allZero = true;
        for (int j = 0; j < n; j++) {
            if (nums[j] != 0) {
                allZero = false;
                break;
            }
        }

        if (allZero) return 0;

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                BitSet temp = (BitSet) dp[j].clone();
                BitSet shifted = new BitSet(1001); // Create a new BitSet for the shifted result
                for (int bitIndex = temp.nextSetBit(0); bitIndex >= 0; bitIndex = temp.nextSetBit(bitIndex + 1)) {
                    if (bitIndex + v < 1001) {
                        shifted.set(bitIndex + v);
                    }
                }
                dp[j].or(shifted);
                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp[j].set(pos, false);
                }
            }

            boolean allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j].get(nums[j])) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;
        }

        return -1;
    }
}

