class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        max_num = 1000001
        dp = [[False] * max_num for _ in range(n)]
        max_sum = [0] * n
        for i in range(n):
            dp[i][0] = True

        is_done = [False] * n

        for i in range(n):
            if nums[i] == 0:
                is_done[i] = True

        if (all(is_done)):
            return 0

        steps = 0
        for l, r, val in queries:
            steps += 1
            for i in range(l, r+1):
                if is_done[i]:
                    continue
                start = min(max_num - val - 1, max_sum[i])
                for k in range(start, -1, -1):
                    max_sum[i] = min(max_num, max_sum[i] + val, nums[i])
                    if dp[i][k] and (not dp[i][k+val]) :
                        dp[i][k+val] = True
                        if k + val == nums[i]:
                            is_done[i] = True
                            break
            if all(is_done):
                return steps
        return -1

