class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)

        def check(k: int) -> bool:
            comp = [[] for _ in range(n)]
            for l, r, v in queries[:k]:
                for i in range(l, r + 1):
                    if v > nums[i]:
                        continue
                    comp[i].append(v)
            for i, target in enumerate(nums):
                if target == 0:
                    continue
                dp = 1
                mask_limit = (1 << (target + 1)) - 1
                for v in comp[i]:
                    dp |= dp << v
                    dp &= mask_limit
                    if (dp >> target) & 1:
                        break
                if not ((dp >> target) & 1):
                    return False
            return True

        left, right = 0, m
        while left <= right:
            mid = (left + right) // 2
            if check(mid):
                right = mid - 1
            else:
                left = mid + 1
        return left if left <= m else -1

