class Solution {
public:
    bool call(int k, vector<int>& a) {
        int n = a.size();
        if (n == 0) return k == 0;  // Return true if k is 0 when no elements are left
        vector<int> b(k + 1, 0);
        b[0] = 1; // Base case: sum of 0 is always achievable

        for (int i = 0; i < n; i++) {
            for (int j = k; j >= a[i]; j--) {
                b[j] = b[j] || b[j - a[i]];
            }
        }
        return b[k];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        unordered_map<int,vector<int>> a;
        int n=nums.size(),s=0;
        for (int i:nums) s+=!(i);
        if (s==n) return 0;
        for (int i=0;i<n;i++) a[i]={};
        for (int i=0;i<q.size();i++) {
            int l=q[i][0];
            int r=q[i][1];
            while (l<=r) {
                a[l].push_back(q[i][2]);
                l++;
            }
            int j=0;
            for (;j<n;j++) if (!call(nums[j],a[j])) break;
            if (j==n) return i+1;
        }
        return -1;
    }
};

