class Solution {
public:
    int solve(vector<pair<int,int>>& arr, int num, int index, vector<vector<int>>& dp){
        if(index >= arr.size() || num < 0){
            return INT_MAX;
        }
        if(num - arr[index].first == 0){
            return arr[index].second;
        }
        // cout<<arr.size()<<" "<<index<<" "<<num<<endl;
        if(dp[index][num] != -1){
            return dp[index][num];
        }
        int take = solve(arr, num - arr[index].first, index+1, dp);
        int notTake = solve(arr, num, index+1, dp);

        return dp[index][num] = min(take , notTake);
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        int n = nums.size();
        for(int i=0; i<n; i++){
            vector<pair<int,int>> ind;
            if(nums[i] == 0){
                continue;
            }
            for(int j =0; j<queries.size(); j++){
                int l = queries[j][0];
                int r = queries[j][1];
                int val = queries[j][2];
                if(i >= l && i<= r && val <= nums[i]){
                    ind.push_back({val, j+1});
                }
            }
            // cout<<nums[i]<<" ";
            // for(int x: ind){
            //     cout<<x<<" ";
            // }cout<<endl;
            vector<vector<int>> dp(ind.size()+1, vector<int>(1001, -1));
            int res = solve(ind, nums[i], 0, dp);
            if(res == INT_MAX){
                return -1;
            }
            ans = max(ans, res);
        }
        return ans;
    }
};

