class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;

        int ans = -1;
        for(int i = 0; i < n; i++) {
            int res = 0;
            int v = nums[i];
            if(v == 0) {
                res = 0;
            } else {
                List<Integer> a = new ArrayList<>();
                for(int[] j : queries) {
                    if(j[0] <= i && j[1] >= i) {
                        a.add(j[2]);
                    } else {
                        a.add(0);
                    }
                }
                // System.out.println(a);
                int[][] dp = new int[a.size()][v+1];
                for(int[] arr : dp) {
                    Arrays.fill(arr, -1);
                }
                res = dp(0, 0, a, v, dp);  
            }
            ans = Math.max(ans, res);

        }
        return ans == Integer.MAX_VALUE ? -1 : ans;
    }

    int dp(int i, int sum, List<Integer> a, int target, int[][] dp) {
        if(sum == target) {
            return i;
        }

        if(sum > target) {
            return Integer.MAX_VALUE;
        }

        if(i >= a.size()) {
            return Integer.MAX_VALUE;
        }

        if(dp[i][sum] != -1) {
            return dp[i][sum];
        }

        int ans1 = dp(i+1, sum + a.get(i), a, target, dp);
        int ans2 = dp(i+1, sum, a, target, dp);
        int ans = Math.min(ans1, ans2);
        dp[i][sum] = ans;
        return ans;
    }
}

