class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = 0;
        for(int i=0;i<nums.length;i++){
            int[][] dp = new int[queries.length][nums[i]+1];
            for(int[] row : dp)
                Arrays.fill(row,-2);
            int res = helper(queries,nums[i],0,i,dp);
            if(res == -1)
                return -1;
            ans = Math.max(ans,res);
        }
        return ans;
    }
    public int helper(int[][] arr,int sum,int idx,int i,int[][] dp){
        if(sum == 0){
            return idx;
        }
        if(idx == arr.length){
            return -1;
        }
        if(dp[idx][sum] != -2)
            return dp[idx][sum];
        int noTake = helper(arr,sum,idx+1,i,dp);
        int take = -1;
        if(i <= arr[idx][1] && i >= arr[idx][0] && sum >= arr[idx][2])
            take = helper(arr,sum-arr[idx][2],idx+1,i,dp);
        if(take == -1 && noTake == -1){
            return dp[idx][sum] = -1;
        }
        else if(take == -1){
            return dp[idx][sum] =  noTake;
        }
        else if(noTake == -1){
            return dp[idx][sum] =  take;
        }
        else{
            return dp[idx][sum] =  Math.min(take,noTake);
        }
    }
}

