using System;
using System.Collections;
using System.Collections.Generic;

public class Solution {
    public int MinZeroArray(int[] arr, int[][] qrs) {
        int n = arr.Length;
        List<BitArray> dp = InitializeDP(n);

        if (CheckAllZero(arr)) return 0;

        for (int i = 0; i < qrs.Length; i++) {
            ProcessQuery(dp, arr, qrs[i]);

            if (CheckValid(dp, arr)) return i + 1;
        }

        return -1;
    }

    private List<BitArray> InitializeDP(int n) {
        List<BitArray> dp = new List<BitArray>(n);
        for (int i = 0; i < n; i++) {
            BitArray b = new BitArray(1001, false);
            b.Set(0, true);
            dp.Add(b);
        }
        return dp;
    }

    private bool CheckAllZero(int[] arr) {
        foreach (var num in arr) {
            if (num != 0) return false;
        }
        return true;
    }

    private void ProcessQuery(List<BitArray> dp, int[] arr, int[] query) {
        int l = query[0], r = query[1], v = query[2];

        for (int j = l; j <= r; j++) {
            dp[j].Or(ShiftLeft(dp[j], v));

            int limit = arr[j] + 1;
            for (int p = limit; p < 1001; p++) {
                dp[j].Set(p, false);
            }
        }
    }

    private bool CheckValid(List<BitArray> dp, int[] arr) {
        for (int j = 0; j < arr.Length; j++) {
            if (!dp[j][arr[j]]) return false;
        }
        return true;
    }

    private BitArray ShiftLeft(BitArray bits, int shift) {
        BitArray shifted = new BitArray(bits.Count);
        for (int i = shift; i < bits.Count; i++) {
            shifted.Set(i, bits[i - shift]);
        }
        return shifted;
    }
}

