class Solution {
    int rec(int idxNum,int num, int idx, vector<vector<int>> &queries, vector<int> &nums,vector<vector<int>> &dp) {
        if(num == 0)
            return idx;
        if(idx >= queries.size()) {
            return INT_MAX;
        }
        int minIdx = INT_MAX;
        if(dp[idx][num] != -1)
                return dp[idx][num];
        //take
        if(queries[idx][0] <= idxNum and queries[idx][1] >= idxNum and num >= queries[idx][2]) {
            minIdx = min(minIdx,rec(idxNum, num - queries[idx][2] , idx + 1,queries,nums,dp));
        }
        //not Take
        minIdx = min(minIdx,rec(idxNum, num, idx + 1, queries, nums,dp));

        return dp[idx][num] = minIdx;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int sol  =0;
        for(int i = 0; i < n; i++) {
            if(nums[i] != 0) {
                 vector<vector<int>> dp(queries.size()+1,vector<int> (nums[i] + 1,-1));

                int reqK = rec(i,nums[i],0,queries,nums,dp);
                if(reqK == INT_MAX)
                    return -1;
                    sol = max(reqK,sol);

            }
        }
        return sol;
    }
};

