class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def canMakeZero(arr, i, sum, memo):
            if sum == 0: return True
            if i <= 0: return False

            if memo[i][sum] != -1:
                return memo[i][sum]

            if arr[i-1] > sum:
                memo[i][sum] = canMakeZero(arr, i-1, sum, memo)
            else:
                memo[i][sum] = canMakeZero(arr, i-1, sum, memo) or canMakeZero(arr, i-1, sum-arr[i-1], memo)
            return memo[i][sum]

        N = len(nums)
        def isZeroArray(k):
            pots = [[] for _ in range(N)]
            for i in range(k):
                l, r, v = queries[i]
                for j in range(l, r+1):
                    if nums[j] >= v:
                        pots[j].append(v)

            # print(f"{k} {pots}")

            for i in range(N):
                pots[i].sort()
                memo = [[-1 for _ in range(nums[i] + 1)] for _ in range(len(pots[i]) + 1)]
                if not canMakeZero(pots[i], len(pots[i]), nums[i], memo):
                    # print(pots)
                    # print(memo)
                    return False

            return True

        if not isZeroArray(len(queries)): return -1

        L, R = 0, len(queries)
        while L <= R:
            M = (L + R) // 2
            if isZeroArray(M):
                R = M - 1
            else:
                L = M + 1
        return L

