ll dp[1001][1001];
class Solution {
public:
    bool dynamic(ll indx,vll &options,ll sum){
        if(sum<0){
            return 0;
        }
        if(sum==0){
            return 1;
        }
        if(indx==arrlen(options)){
            return 0;
        }
        if(dp[indx][sum]!=-1){
            return dp[indx][sum];
        }
        return dp[indx][sum]=dynamic(indx+1,options,sum-options[indx])|dynamic(indx+1,options,sum);
    }
    int minZeroArray(vector<int>& c, vector<vector<int>>& q) {
        ll n=arrlen(c),m=arrlen(q);
        vll ansarr;
        forn(i,n){
            ll l=0,r=m,mid,ans=-1;
            while(l<=r){
                mid=(l+r)/2;
                vll options;
                forn(j,mid){
                    if(i>=q[j][0] and i<=q[j][1]){
                        options.pb(q[j][2]);
                    }
                }
                forn(x,1001){
                    forn(y,1001){
                        dp[x][y]=-1;
                    }
                }
                bool answer=dynamic(0,options,c[i]);
                if(answer){
                    ans=mid;
                    r=mid-1;
                }
                else{
                    l=mid+1;
                }
            }
            if(ans==-1){
                return -1;
            }
            ansarr.pb(ans);
        }
        return *max_element(all(ansarr));
    }
};

