class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, q = len(nums), len(queries)
        data = (nums.copy(), queries.copy())
        dp = [1 for _ in range(n)]  
        masks: List[int] = []
        for j in range(n): masks.append((1 << (nums[j] + 1)) - 1)
        all_done = True
        for j in range(n):
            if nums[j] != 0:
                all_done = False
                break
        if all_done: return 0

        for i, (l, r, val) in enumerate(queries):
            for j in range(l, r + 1):
                dp[j] = dp[j] | ((dp[j] << val) & masks[j])    
            all_possible = True
            for j in range(n):
                if (dp[j] >> nums[j]) & 1 == 0:
                    all_possible = False
                    break
            if all_possible: return i + 1
        return -1

