class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean isZeroArray = true;
        for(int i=0;i<n;i++) {
            if(nums[i]!=0) {
                isZeroArray = false;
                break;
            } 
        }

        if(isZeroArray) return 0;

        int max = 0;
        for(int i=0;i<n;i++) {
            if(nums[i]==0) continue;
            Integer dp[][] = new Integer[queries.length][nums[i]+1];
            int zeroInd = findZeroInd(nums[i], i, 0, queries, 1001, dp);
            if(zeroInd>=1001) return -1;
            max = Math.max(max, zeroInd);
        }

        return max;
    }

    public int findZeroInd(int target, int ind, int curInd, int[][] queries, int lastIndex, Integer dp[][]) {
        if(target==0) return lastIndex;
        if(curInd>=queries.length) return 1001;
        if(dp[curInd][target]!=null) return dp[curInd][target];
        if(queries[curInd][0]>ind || queries[curInd][1]<ind) return findZeroInd(target,ind,curInd+1,queries,lastIndex,dp);
        int ans = findZeroInd(target,ind,curInd+1,queries,lastIndex,dp);
        if(queries[curInd][2]<=target) {
            ans = Math.min(ans, findZeroInd(target-queries[curInd][2],ind,curInd+1,queries,curInd+1,dp));
        }
        return dp[curInd][target] = ans;
    }
}

