class Solution {
public:
    bool match1(vector<int> &b,vector<unordered_set<int>> &vm){
        int n = b.size();
        for(int i  = 0;i <n;i++){
            if(b[i] == 0) continue;
            if(vm[i].find(b[i]) == vm[i].end()){
                return false;
            }
            // if(a[i] < b[i]){
            //     return false;
            // }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        // vector<int> prefix(n,0);
        int m = queries.size();
        vector<unordered_set<int>> vm(n);
        if(match1(nums,vm)) return 0;
        for(int i = 0;i <m;i++){
            int l = queries[i][0], r = queries[i][1],val = queries[i][2];
            for(int j = l; j <= r; j++){
                unordered_set<int> temp = vm[j];
                for (int x : temp) {
                    vm[j].insert(x + val);
                }
                vm[j].insert(val);
            }
            if(match1(nums,vm)) return i+1;
        }
        return -1;
    }
};

