class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool flag = false;
        int n = nums.size();
        for (auto x : nums) {
            if (x != 0) {
                flag = true;
                break;
            }
        }
        function<bool(int)> checker = [&](int mid) -> bool {
            for (int i = 0; i < n; i++) {

                int key = nums[i];
                vector<int> dp(key + 1);
                dp[0] = 1;
                for (int j = 0; j < mid; j++) {
                    if (queries[j][0] <= i && i <= queries[j][1]) {
                        int temp = queries[j][2];
                        for (int kk = key; kk >= temp; kk--) {
                            dp[kk] = dp[kk] | dp[kk - temp];
                        }
                    }
                }
                if (!dp[key]) {
                    return false;
                }
            }
            return true;
        };
        if (flag) {
            int lo = 1, hi = queries.size(), ans = -1;
            while (lo <= hi) {
                int mid = lo + (hi - lo) / 2;
                if (checker(mid)) {
                    ans = mid;
                    hi = mid - 1;
                } else {
                    lo = mid + 1;
                }
            }
            return ans;
        } else {
            return 0;
        }
    }
};

