class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n=len(nums)
        dp=[set() for _ in range(n)]
        for j in range(n):
            dp[j].add(0)

        all_z=all(x==0 for x in nums)
        if all_z:
            return 0

        for k in range(len(queries)):
            l,r,v=queries[k]
            for j in range(l,r+1):
                shift=set()

                for i in range(1001-v):
                    if i in dp[j]:
                        shift.add(i+v)

                dp[j].update(shift)
                for pos in range(nums[j]+1,1001):
                    dp[j].discard(pos)

            all_achi=all(nums[j] in dp[j] for j in range(n))
            if all_achi:
                return k+1
        return -1

