class Solution {
public:
    int helpr(vector<vector<int>>& q,int index,int target,int& ti,vector<vector<int>>& dp){
        if(target==0)return index;
        if(index==q.size() || target<0)return 1e9;
        if(dp[index][target]!=-1)return dp[index][target];

        int ans=1e9;
        if(ti>=q[index][0] && ti<=q[index][1])ans=min(ans,helpr(q,index+1,target-q[index][2],ti,dp));
        ans=min(ans,helpr(q,index+1,target,ti,dp));
        return dp[index][target]=ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int ans=0;

        for(int i=0;i<nums.size();i++){
            vector<vector<int>>dp(q.size(),vector<int>(1001,-1));
            int temp=helpr(q,0,nums[i],i,dp);
            ans=max(ans,temp);
            if(ans==1e9)return -1;
        }
        return ans;
    }
};

